import React from 'react';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';

const images = [
  { src: 'https://drive.google.com/file/d/1gZ0gQiRHIpJv7htvOzAWvpLyjHLPC8aC/view?usp=drive_link', link: 'https://www.https://youtu.be/nr7Cd2wFqsA.com/watch?v=YOUR_VIDEO_ID_1' },
  { src: 'https://drive.google.com/file/d/1ra-CwVPGjIs6iRO9E8ki5K2PIUDtSMXb/view?usp=drive_link', link: 'https://www.youtube.com/https://www.youtube.com/live/wqkgQUKGapI?si=sTbosLKGp9em6Zxn?v=YOUR_VIDEO_ID_2' },
  // Add more images and links here
];

const SermonsSlideshow = () => {
  return (
    <div className="slideshow-container">
      <Slide>
        {images.map((image, index) => (
          <div className="each-slide" key={index}>
            <a href={image.link} target="_blank" rel="noopener noreferrer">
              <img src={image.src} alt={`Slide ${index}`} style={{ width: '100%' }} />
            </a>
          </div>
        ))}
      </Slide>
    </div>
  );
};

export default SermonsSlideshow;
