import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock, faPray, faBible, faMoon } from '@fortawesome/free-solid-svg-icons';
import '../index.css'; // Ensure your index.css has necessary styles

const OurServices = () => {
  return (
    <div className="our-services-container">
      <h2>Our Services</h2>
      <div className="grid-container">
        <div className="card">
          <h3>Sunday Services</h3>
          <p>
            <FontAwesomeIcon icon={faClock} /> Youth Online Service: 7:30am - 8:30am<br />
            <FontAwesomeIcon icon={faClock} /> 1st Service: 9:00am - 10:30am<br />
            <FontAwesomeIcon icon={faClock} /> 2nd Service: 11:00am - 1:30pm
          </p>
        </div>
        <div className="card">
          <h3>Midweek Services</h3>
          <p>
            <FontAwesomeIcon icon={faPray} /> Evening Prayers Monday: 5:30pm - 7:00pm<br />
            <FontAwesomeIcon icon={faBible} /> Bible Study Wednesday: 5:30pm - 7:00pm<br />
            <FontAwesomeIcon icon={faMoon} /> Overnight Prayer Vigil Kesha Friday: 9:00pm - 5:00am
          </p>
        </div>
      </div>
    </div>
  );
}

export default OurServices;
